function loadLinhVuc(n){window.AjaxDungChung("post","/DesktopModules/MVC/NghiepVu/HoSo/GetDanhMucLinhVuc",window.moduleId,window.tabId,$("input[name='__RequestVerificationToken']").val(),null,"application/json; charset=utf-8","json",!0).then(function(t){if(t){var i=[];$.each(t,(n,t)=>{i.push({ID:t.LinhVucID,Ten:t.TenLinhVuc})});window.LoadSelect2($(".sl_Linh_Vuc"),i,n?n:null,!0)}})}function loadThuTuc(n,t){if(isNaN(n*1))window.LoadSelect2($(".sl_Thu_Tuc"),[],null,!0);else{var i=JSON.stringify({linhvucID:n});window.AjaxDungChung("post","/DesktopModules/MVC/NghiepVu/HoSo/GetThuTucByLinhVucIDJsonResult",window.moduleId,window.tabId,$("input[name='__RequestVerificationToken']").val(),i,"application/json; charset=utf-8","json",!0).then(n=>{if(n){var i=[];$.each(n,(n,t)=>{i.push({ID:t.ThuTucID,Ten:t.TenThuTuc})});window.LoadSelect2($(".sl_Thu_Tuc"),i,t?t:null,!0)}})}}$(document).ready(()=>{loadLinhVuc(),window.LoadSelect2($(".sl_Thu_Tuc"),[],null,!0),window.InitFromToDatePicker(".date-picker-from",".date-picker-to")});$(".sl_Linh_Vuc").change(n=>{var i=$(n.target),t=n.target.value;loadThuTuc(t)});